// Auth Context
const AuthContext = createContext();

const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('access_token');
    if (token) {
      // Verify token and get user info
      fetchUserInfo(token);
    } else {
      setLoading(false);
    }
  }, []);

  const fetchUserInfo = async (token) => {
    try {
      const response = await axios.get(`${API}/auth/me`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // /api/auth/me returns { success: true, user: {...} }
      if (response.data.success && response.data.user) {
        setUser(response.data.user);
      } else {
        throw new Error('Invalid response format');
      }
    } catch (error) {
      console.error('Token verification failed:', error);
      localStorage.removeItem('access_token');
    } finally {
      setLoading(false);
    }
  };

  const login = async (username, password) => {
    try {
      console.log('ğŸ”‘ login function called with:', username, password?.length, 'chars');
      console.log('ğŸ”‘ API URL:', `${API}/auth/login`);
      
      const response = await axios.post(`${API}/auth/login`, {
        username,
        password
      });
      
      console.log('âœ… Login response received:', response.status, response.data);
      
      const { access_token, user: userData } = response.data;
      console.log('ğŸ”‘ Extracted data:', { access_token: access_token?.substring(0, 20) + '...', userData });
      
      localStorage.setItem('access_token', access_token);
      console.log('ğŸ”‘ Token stored in localStorage');
      
      console.log('ğŸ”‘ About to call setUser with:', userData);
      console.log('ğŸ”‘ Current user state before setUser:', user);
      setUser(userData);
      console.log('ğŸ”‘ setUser called - user state should update now');
      
      // Force a re-render by logging the state change
      setTimeout(() => {
        console.log('ğŸ”‘ User state after setUser (500ms later):', user);
      }, 500);
      
      return { success: true };
    } catch (error) {
      console.error('âŒ Login error:', error);
      console.error('âŒ Error response:', error.response?.data);
      return { 
        success: false, 
        error: error.response?.data?.detail || 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø¯Ø®ÙˆÙ„'
      };
    }
  };

  const logout = () => {
    localStorage.removeItem('access_token');
    setUser(null);
  };

  const value = {
    user,
    login,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

