// Selfie Capture Component for Sales Reps
const SelfieCapture = ({ onCapture, onSkip }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [capturing, setCapturing] = useState(false);
  const { language } = useContext(ThemeContext);

  const translations = {
    en: {
      title: "üì∑ Daily Check-in Selfie",
      subtitle: "Please take a selfie to verify your attendance",
      startCamera: "üé• Start Camera",
      takeSelfie: "üì∏ Take Selfie",
      retake: "üîÑ Retake",
      confirm: "‚úÖ Confirm",
      skip: "‚è≠Ô∏è Skip for Now",
      cameraError: "Cannot access camera. Please allow camera permissions."
    },
    ar: {
      title: "üì∑ ÿ≥ŸäŸÑŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ≠ÿ∂Ÿàÿ± ÿßŸÑŸäŸàŸÖŸä",
      subtitle: "Ÿäÿ±ÿ¨Ÿâ ÿ£ÿÆÿ∞ ÿ≥ŸäŸÑŸÅŸä ŸÑŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∂Ÿàÿ±ŸÉ",
      startCamera: "üé• ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß",
      takeSelfie: "üì∏ ÿßŸÑÿ™ŸÇÿßÿ∑ ÿ≥ŸäŸÑŸÅŸä",
      retake: "üîÑ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ŸÇÿßÿ∑",
      confirm: "‚úÖ ÿ™ÿ£ŸÉŸäÿØ",
      skip: "‚è≠Ô∏è ÿ™ÿÆÿ∑Ÿä ÿßŸÑÿ¢ŸÜ",
      cameraError: "ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑŸàÿµŸàŸÑ ŸÑŸÑŸÉÿßŸÖŸäÿ±ÿß. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ≥ŸÖÿßÿ≠ ÿ®ÿµŸÑÿßÿ≠Ÿäÿßÿ™ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß."
    }
  };

  const t = translations[language] || translations.en;

  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { facingMode: 'user', width: 640, height: 480 } 
      });
      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
    } catch (error) {
      console.error('Error accessing camera:', error);
      alert(t.cameraError);
    }
  };

  const takeSelfie = () => {
    if (canvasRef.current && videoRef.current) {
      const canvas = canvasRef.current;
      const video = videoRef.current;
      const context = canvas.getContext('2d');
      
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      context.drawImage(video, 0, 0);
      
      const imageData = canvas.toDataURL('image/jpeg', 0.8);
      setCapturing(true);
      
      // Save selfie to backend
      saveSelfie(imageData);
    }
  };

  const saveSelfie = async (imageData) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API}/users/selfie`, {
        selfie_image: imageData,
        timestamp: new Date().toISOString(),
        location: await getCurrentLocation()
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (onCapture) onCapture(imageData);
    } catch (error) {
      console.error('Error saving selfie:', error);
    }
  };

  const getCurrentLocation = () => {
    return new Promise((resolve) => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => resolve({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          }),
          () => resolve({ latitude: null, longitude: null })
        );
      } else {
        resolve({ latitude: null, longitude: null });
      }
    });
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
  };

  useEffect(() => {
    return () => stopCamera();
  }, []);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50">
      <div className="modal-modern p-8 w-full max-w-md">
        <div className="text-center mb-6">
          <h3 className="text-2xl font-bold text-gradient mb-2">{t.title}</h3>
          <p className="text-sm" style={{ color: 'var(--text-secondary)' }}>
            {t.subtitle}
          </p>
        </div>

        <div className="space-y-4">
          <div className="relative bg-black rounded-lg overflow-hidden" style={{ aspectRatio: '4/3' }}>
            {stream ? (
              <>
                <video
                  ref={videoRef}
                  autoPlay
                  playsInline
                  muted
                  className="w-full h-full object-cover"
                />
                <div className="absolute inset-4 border-2 border-green-400 rounded-lg"></div>
              </>
            ) : (
              <div className="flex items-center justify-center h-full text-white">
                <div className="text-center">
                  <div className="text-6xl mb-4">üì∑</div>
                  <button
                    onClick={startCamera}
                    className="btn-primary"
                  >
                    {t.startCamera}
                  </button>
                </div>
              </div>
            )}
          </div>
          
          <canvas ref={canvasRef} style={{ display: 'none' }} />
          
          {stream && (
            <div className="flex gap-3">
              <button
                onClick={takeSelfie}
                className="btn-success flex-1 flex items-center justify-center gap-2"
              >
                <span>üì∏</span>
                <span>{t.takeSelfie}</span>
              </button>
            </div>
          )}

          <div className="flex gap-3 pt-4 border-t border-gray-200">
            <button
              onClick={() => {
                stopCamera();
                if (onSkip) onSkip();
              }}
              className="btn-secondary flex-1"
            >
              {t.skip}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

